type DonationContract = Contract & {
  donate: (amount: number, id: number) => Promise<any>;
};

function useContracts() {
  const {providerState} = useEtherProvider();
  const provider = providerState.status === "connected" ? providerState.provider : null;
  const signer = provider ? provider.getSigner() : null;
  const tokenContract = useMemo(() => {
    if (!signer) throw new Error("Signer not available");

    return new ethers.Contract(TOKEN_PROXY_CONTRACT_ADDRESS, TOKEN_PROXY_ABI, signer);
  }, [signer]);

  const donationContract = useMemo(() => {
    if (!signer) throw new Error("Signer not available");

    return new ethers.Contract(
      DONATION_CONTRACT_ADDRESS,
      DONATION_CONTRACT_ABI,
      signer
    ) as DonationContract;
  }, [signer]);

  const nftContract = useMemo(() => {
    if (!signer) throw new Error("Signer not available");

    return new ethers.Contract(NFT_CONTRACT_ADDRESS, NFT_CONTRACT_ABI, signer);
  }, [signer]);

  // @ts-ignore
  window.contracts = {
    tokenContract,
    donationContract,
    nftContract
  };
  return {
    tokenContract,
    donationContract,
    nftContract
  };
}

function useDonationContractMethods() {
  const {donationContract} = useContracts();

  const donate = useCallback(
    async (amount: number, id: number) => {
      const tx = await (await donationContract.donate(amount, id)).wait();
      return tx;
    },
    [donationContract]
  );

  return {
    donate
  };
}

function useTokenContractMethods() {
  const {tokenContract} = useContracts();

  const approve = useCallback(
    async (amount: number) => {
      const tx = await (
        await tokenContract.approve(DONATION_CONTRACT_ADDRESS, amount)
      ).wait();
      return tx;
    },
    [tokenContract]
  );

  return {
    approve
  };
}

export {
  DONATION_CONTRACT_ADDRESS,
  DONATION_CONTRACT_ABI,
  TOKEN_PROXY_CONTRACT_ADDRESS,
  TOKEN_PROXY_ABI,
  //-------------------//
  useContracts,
  useDonationContractMethods,
  useTokenContractMethods
};
